sys_script.script:
(function executeRule(current, previous /*null when async*/) {

// Add your code here
var vehStatus = current.vehicle_status; //Create a variable for the current vehicle status field
var lvCatalog = new GlideRecord ('x_1515300_lva_loaner_vehicle_catalog'); //Create a variable for a new GlideRecord on the loaner vehicle catalog table

lvCatalog.addQuery('sys_id', current.vehicle); //Query that table for the sys_id for the current vehicle field
lvCatalog.query();



 // 4. Iterate through the loaner vehicle catalog table and check the status
    if (lvCatalog.next()) {

		// 5. Check the vehicle status in the tracker table and update the status in the loaner vehicle catalog
        if (vehStatus == 'Pending Release' || vehStatus == 'Out on Field' || 
            vehStatus == 'Returned for Inspection' || vehStatus == 'Sent for Servicing') 
			{ 
				lvCatalog.status = 'Unavailable';

    // If the vehicle status is returned to warehouse, set the loaner vehicle to available
        } else if (vehStatus == 'Returned to Warehouse') {

            lvCatalog.status = 'Available';
	// If the vehicle status is decommissioned, set the loaner vehicle to decommissioned
        } else if (vehStatus == 'Decommissioned') {

            lvCatalog.status = 'Decommissioned';
        }

        // 6. Update the status on the loaner vehicle catalog table
        lvCatalog.update(); // Commit the change to the record
	}

})(current, previous);
